export function validateForm(form){ const errors = {}; const nome = form.nome?.value?.trim() || ''; const email = form.email?.value?.trim() || ''; if(nome.length < 3) errors.nome = 'Nome deve ter ao menos 3 caracteres.'; if(!/^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(email)) errors.email = 'Email invÃ¡lido.'; return errors; } export function showErrors(form, errors){ form.querySelectorAll('[data-error-for]').forEach(el=>el.textContent=''); Object.keys(errors).forEach(k=>{ const el = form.querySelector(`[data-error-for="${k}"]`); if(el) el.textContent = errors[k]; }); } export function clearErrors(form){ form.querySelectorAll('[data-error-for]').forEach(el=>el.textContent=''); }