export function homeTemplate(){ return ` <section class="card" aria-labelledby="home-title"><h1 id="home-title">Bem-vinda(o) à Minha Conta</h1> <p class="small">Esta versão final inclui acessibilidade (WCAG AA), modo escuro, alto contraste, navegação por teclado e produção otimizada.</p></section>`;} export function sobreTemplate(){ return `<section class="card" aria-labelledby="sobre-title"><h2 id="sobre-title">Sobre o projeto</h2> <p class="small">Entrega Final — Implementação profissional com GitFlow, releases e documentação.</p></section>`;} export function cadastroTemplate(saved=[]){ const rows = saved.length===0?'<p class="small">Nenhum usuário cadastrado.</p>':saved.map((u,i)=>`<div class="list-item"><strong>${escapeHtml(u.nome)}</strong> — <span class="small">${escapeHtml(u.email)}</span><div class="actions"><button class="link-like" data-action="delete" data-index="${i}">Excluir</button></div></div>`).join(''); return ` <section class="card" aria-labelledby="cadastro-title"><h2 id="cadastro-title">Cadastro de Usuários</h2> <form id="userForm" novalidate aria-describedby="form-help"><div class="form-row"><label for="nome">Nome</label><input id="nome" name="nome" minlength="3" required aria-required="true"/><div class="error" data-error-for="nome" aria-live="polite"></div></div><div class="form-row"><label for="email">Email</label><input id="email" name="email" type="email" required aria-required="true"/><div class="error" data-error-for="email" aria-live="polite"></div></div><button class="btn" type="submit">Salvar</button></form><p id="form-help" class="small">Os campos marcados são obrigatórios.</p><h3 style="margin-top:16px">Usuários salvos</h3><div id="savedList">${rows}</div></section>`;} function escapeHtml(str=''){return String(str).replace(/[&<>"']/g,m=>({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;',"'":'&#39;'})[m]);}